@Composable
fun AddExerciseDialog(
 onDismiss: () -> Unit,
 onAddExercise: (ExerciseItem) -> Unit
) {
 var exerciseName by remember { mutableStateOf("") }
 var duration by remember { mutableStateOf("") }
 var caloriesBurned by remember { mutableStateOf("") }

