 modifier = modifier
 .fillMaxSize()
 .background(Color(0xFFF5F5F5))
 .padding(16.dp),
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 item {
 CaloriesTrackerCard(
 eaten = viewModel.totalCaloriesEaten,
 burned = viewModel.totalCaloriesBurned,
 remaining = viewModel.remainingCalories,
 goal = goal
 )
 }

