 DateSelector()
 }

 item {
 FoodDiarySection(
 foodItems = entry.foodItems,
 waterIntake = entry.waterIntake,
 onAddFood = onAddFood,
 onUpdateWater = { viewModel.updateWaterIntake(it) }
 )
 }

 item {
 ExerciseDiarySection(
 exercises = entry.exercises,
 onAddExercise = onAddExercise
 )
 }

 item {
 AddItemSection(
 onAddFood = onAddFood,
 onAddExercise = onAddExercise
 )
 }
 }
}

