@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun AddFoodScreen(
 onDismiss: () -> Unit,
 onAddFood: (FoodItem) -> Unit,
 defaultCategory: String = "Breakfast"
) {
 val foodRepository = remember { FoodRepository() }
 var currentView by remember { mutableStateOf("list") } // "list" or "detail"
 var selectedFood by remember { mutableStateOf<FoodDatabase?>(null) }
 var searchQuery by remember { mutableStateOf("") }
 var selectedTab by remember { mutableStateOf("All") }

 Column(
 modifier = Modifier
 .fillMaxSize()
 .background(Color.White)
 ) {
 // Header
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 IconButton(onClick = onDismiss) {
 Icon(Icons.Default.Close, contentDescription = "Close")
 }
 Text(
 text = "Add Food",
 fontSize = 20.sp,
 fontWeight = FontWeight.Bold,
 color = Color.White,
 modifier = Modifier
 .background(
 Color.Gray,
 RoundedCornerShape(8.dp)
 )
 .padding(horizontal = 16.dp, vertical = 8.dp)
 )
 Spacer(modifier = Modifier.width(48.dp))
 }

 if (currentView == "list") {
 FoodListView(
 searchQuery = searchQuery,
 onSearchQueryChange = { searchQuery = it },
 selectedTab = selectedTab,
 onTabChange = { selectedTab = it },
 foodRepository = foodRepository,
 onFoodSelect = { food ->
 selectedFood = food
 currentView = "detail"
 }
 )
 } else if (currentView == "detail" && selectedFood != null) {
 FoodDetailView(
 food = selectedFood!!,
 defaultCategory = defaultCategory,
 onBack = { currentView = "list" },
 onAddToLog = onAddFood
 )
 }
 }
}

@Composable
fun FoodListView(
 searchQuery: String,
 onSearchQueryChange: (String) -> Unit,
 selectedTab: String,
 onTabChange: (String) -> Unit,
 foodRepository: FoodRepository,
 onFoodSelect: (FoodDatabase) -> Unit
) {
 Column {
 // Search Bar
 OutlinedTextField(
 value = searchQuery,
 onValueChange = onSearchQueryChange,
 placeholder = { Text("Search") },
 leadingIcon = { Icon(Icons.Default.Search, contentDescription = "Search") },
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp),
 colors = OutlinedTextFieldDefaults.colors(
 focusedBorderColor = Color(0xFF4CAF50),
 unfocusedBorderColor = Color.Gray
 )
 )

 Spacer(modifier = Modifier.height(16.dp))

 // Tabs
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp),
 horizontalArrangement = Arrangement.SpaceEvenly
 ) {
 listOf("All", "Favourite", "Custom").forEach { tab ->
 Text(
 text = tab,
 color = if (selectedTab == tab) Color(0xFF4CAF50) else Color.Gray,
 fontWeight = if (selectedTab == tab) FontWeight.Bold else FontWeight.Normal,
 modifier = Modifier
 .clickable { onTabChange(tab) }
 .padding(8.dp)
 )
