 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Text("Result", fontWeight = FontWeight.Medium)
 Row(verticalAlignment = Alignment.CenterVertically) {
 Icon(Icons.Default.KeyboardArrowUp, contentDescription = "Sort")
 Text("Most Relevant", fontSize = 14.sp, color = Color.Gray)
 }
 }

 Spacer(modifier = Modifier.height(8.dp))

 // Food Items
 val foods = when (selectedTab) {
 "Favourite" -> foodRepository.getFavorites()
 "Custom" -> foodRepository.getCustomFoods()
 else -> foodRepository.searchFoods(searchQuery)
 }

 LazyColumn(
 modifier = Modifier.weight(1f)
 ) {
