 fat = food.nutritionPer100g.fat * multiplier,
 fiber = food.nutritionPer100g.fiber * multiplier,
 sugar = food.nutritionPer100g.sugar * multiplier,
 sodium = food.nutritionPer100g.sodium * multiplier,
 cholesterol = food.nutritionPer100g.cholesterol * multiplier
 )
 )
 onAddToLog(foodItem)
 },
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 colors = ButtonDefaults.buttonColors(
 containerColor = Color(0xFF4CAF50)
 )
 ) {
 Text("Add To Diary", color = Color.White, fontSize = 16.sp)
 }
 }
}

@Composable
fun MacroRow(
 name: String,
 percentage: String,
 amount: String,
 color: Color
) {
 Row(
 verticalAlignment = Alignment.CenterVertically,
 modifier = Modifier.padding(vertical = 2.dp)
 ) {
 Box(
 modifier = Modifier
 .size(8.dp)
 .background(color, RoundedCornerShape(4.dp))
 )
 Spacer(modifier = Modifier.width(8.dp))
 Text(
 text = "$name ($percentage) - $amount",
 fontSize = 12.sp
 )
 }
}

@Composable
fun NutrientRow(
 name: String,
 amount: String
) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 4.dp),
 horizontalArrangement = Arrangement.SpaceBetween
 ) {
 Text(text = name, fontSize = 14.sp)
 Text(text = amount, fontSize = 14.sp, color = Color.Gray)
 }
}

// Helper function for sorting foods
fun sortFoods(foods: List<FoodDatabase>, sortBy: String): List<FoodDatabase> {
 return when (sortBy) {
 "A-Z" -> foods.sortedBy { it.name }
 "Z-A" -> foods.sortedByDescending { it.name }
 "Recently Added" -> foods.reversed() // Assuming last added items are at the end
 else -> foods // Most Relevant (default order)
 }
}

