+@Composable
+fun ExerciseListView(
+ searchQuery: String,
+ onSearchQueryChange: (String) -> Unit,
+ selectedTab: String,
+ onTabChange: (String) -> Unit,
+ exerciseRepository: ExerciseRepository,
+ onExerciseSelect: (ExerciseDatabase) -> Unit
+) {
+ var sortBy by remember { mutableStateOf("Most Relevant") }
+ var showSortMenu by remember { mutableStateOf(false) }
+ val sortOptions =
+ listOf("Most Relevant", "A-Z", "Z-A", "Calories High to Low", "Calories Low to High")
+
+ Column {
+ // Search Bar
+ OutlinedTextField(
