 onAddFood = { food ->
 viewModel.addFoodItem(food)
 showAddFood = false
 }
 )
 }

 if (showAddExercise) {
 AddExerciseDialog(
 onDismiss = { showAddExercise = false },
 onAddExercise = { exercise ->
 viewModel.addExercise(exercise)
 showAddExercise = false
 }
 )
 }
 }
