@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun AddExerciseScreen(
 onDismiss: () -> Unit,
 onAddExercise: (ExerciseItem) -> Unit
) {
 val exerciseRepository = remember { ExerciseRepository() }
 var currentView by remember { mutableStateOf("list") } // "list" or "detail"
 var selectedExercise by remember { mutableStateOf<ExerciseDatabase?>(null) }
 var searchQuery by remember { mutableStateOf("") }
 var selectedTab by remember { mutableStateOf("All") }

 Column(
 modifier = Modifier
 .fillMaxSize()
 .background(Color.White)
 ) {
 // Header
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 IconButton(onClick = onDismiss) {
 Icon(Icons.Default.Close, contentDescription = "Close")
 }
 Text(
 text = "Add Exercise",
 fontSize = 20.sp,
 fontWeight = FontWeight.Bold,
 color = Color.White,
 modifier = Modifier
 .background(
 Color.Gray,
 RoundedCornerShape(8.dp)
 )
 .padding(horizontal = 16.dp, vertical = 8.dp)
 )
 Spacer(modifier = Modifier.width(48.dp))
 }

 if (currentView == "list") {
 ExerciseListView(
 searchQuery = searchQuery,
 onSearchQueryChange = { searchQuery = it },
 selectedTab = selectedTab,
 onTabChange = { selectedTab = it },
 exerciseRepository = exerciseRepository,
 onExerciseSelect = { exercise ->
 selectedExercise = exercise
 currentView = "detail"
 }
 )
 } else if (currentView == "detail" && selectedExercise != null) {
 ExerciseDetailView(
 exercise = selectedExercise!!,
 onBack = { currentView = "list" },
 onAddToLog = onAddExercise,
 exerciseRepository = exerciseRepository
 )
 }
 }
}

@Composable
fun ExerciseListView(
 searchQuery: String,
 onSearchQueryChange: (String) -> Unit,
 selectedTab: String,
 onTabChange: (String) -> Unit,
 exerciseRepository: ExerciseRepository,
 onExerciseSelect: (ExerciseDatabase) -> Unit
) {
 var sortBy by remember { mutableStateOf("Most Relevant") }
 var showSortMenu by remember { mutableStateOf(false) }
 val sortOptions =
 listOf("Most Relevant", "A-Z", "Z-A", "Calories High to Low", "Calories Low to High")

 Column {
 // Search Bar
 OutlinedTextField(
 value = searchQuery,
 onValueChange = onSearchQueryChange,
