 verticalAlignment = Alignment.CenterVertically
 ) {
 Text("Result", fontWeight = FontWeight.Medium)
 Row(
 verticalAlignment = Alignment.CenterVertically,
 modifier = Modifier.clickable { showSortMenu = true }
 ) {
 Icon(Icons.Default.KeyboardArrowUp, contentDescription = "Sort")
 Text(sortBy, fontSize = 14.sp, color = Color.Gray)

 DropdownMenu(
 expanded = showSortMenu,
 onDismissRequest = { showSortMenu = false }
 ) {
 sortOptions.forEach { option ->
 DropdownMenuItem(
 text = { Text(option) },
 onClick = {
 sortBy = option
 showSortMenu = false
 }
 )
 }
 }
 }
 }

 Spacer(modifier = Modifier.height(8.dp))

 // Exercise Items
 val exercises = when (selectedTab) {
 "Favourite" -> {
 val favorites = exerciseRepository.getFavorites()
 val filteredFavorites = if (searchQuery.isEmpty()) {
 favorites
 } else {
 favorites.filter { it.name.contains(searchQuery, ignoreCase = true) }
 }
 sortExercises(filteredFavorites, sortBy)
 }

 else -> sortExercises(exerciseRepository.searchExercises(searchQuery), sortBy)
 }

 LazyColumn(
 modifier = Modifier.weight(1f)
 ) {
 items(exercises) { exercise ->
 ExerciseItemRow(
 exercise = exercise,
 onClick = { onExerciseSelect(exercise) }
 )
 }
 }
 }
}

