// Helper function for sorting exercises
fun sortExercises(exercises: List<ExerciseDatabase>, sortBy: String): List<ExerciseDatabase> {
 return when (sortBy) {
 "A-Z" -> exercises.sortedBy { it.name }
 "Z-A" -> exercises.sortedByDescending { it.name }
 "Calories High to Low" -> exercises.sortedByDescending { it.caloriesPer60Min }
 "Calories Low to High" -> exercises.sortedBy { it.caloriesPer60Min }
 else -> exercises // Most Relevant (default order)
 }
}

