@Composable
fun FoodDiarySection(
 foodItems: List<FoodItem>,
 waterIntake: Int,
 onAddFood: () -> Unit,
 onUpdateWater: (Int) -> Unit
) {
 Card(
 modifier = Modifier.fillMaxWidth(),
 colors = CardDefaults.cardColors(containerColor = Color.White)
 ) {
 Column(modifier = Modifier.padding(16.dp)) {
 Text(
 "EAT",
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 16.dp)
 )

 // Water intake
 DiaryItem(
 icon = Icons.Default.Home,
 title = "Water",
 subtitle = "$waterIntake / 64 fl oz",
 calories = null,
 onAdd = { onUpdateWater(waterIntake + 8) }
 )

 // Breakfast
 DiaryItem(
 icon = Icons.Default.Star,
 title = "Breakfast",
 subtitle = if (foodItems.any { it.category == "Breakfast" })
 foodItems.filter { it.category == "Breakfast" }.joinToString(", ") { it.name }
 else "Add breakfast items",
 calories = foodItems.filter { it.category == "Breakfast" }.sumOf { it.calories }
 .takeIf { it > 0 },
 onAdd = onAddFood
 )

 // Lunch
 DiaryItem(
 icon = Icons.Default.FavoriteBorder,
 title = "Lunch",
 subtitle = if (foodItems.any { it.category == "Lunch" })
 foodItems.filter { it.category == "Lunch" }.joinToString(", ") { it.name }
 else "Add lunch items",
 calories = foodItems.filter { it.category == "Lunch" }.sumOf { it.calories }
 .takeIf { it > 0 },
 onAdd = onAddFood
 )

 // Dinner
 DiaryItem(
 icon = Icons.Default.Place,
 title = "Dinner",
 subtitle = if (foodItems.any { it.category == "Dinner" })
 foodItems.filter { it.category == "Dinner" }.joinToString(", ") { it.name }
 else "Add dinner items",
 calories = foodItems.filter { it.category == "Dinner" }.sumOf { it.calories }
 .takeIf { it > 0 },
 onAdd = onAddFood
 )

 // Snack
 DiaryItem(
 icon = Icons.Default.Favorite,
 title = "Snack",
 subtitle = if (foodItems.any { it.category == "Snack" })
 foodItems.filter { it.category == "Snack" }.joinToString(", ") { it.name }
 else "Add snack items",
 calories = foodItems.filter { it.category == "Snack" }.sumOf { it.calories }
 .takeIf { it > 0 },
 onAdd = onAddFood
 )

 // Add more button
 TextButton(
 onClick = onAddFood,
 modifier = Modifier.fillMaxWidth()
 ) {
 Icon(Icons.Default.Add, contentDescription = "Add")
 Spacer(modifier = Modifier.width(8.dp))
 Text("Add Categories")
 }
 }
 }
}

@Composable
fun ExerciseDiarySection(
 exercises: List<ExerciseItem>,
 onAddExercise: () -> Unit
) {
 Card(
