@Composable
fun DiaryItem(
 icon: ImageVector,
 title: String,
 subtitle: String,
 calories: Int?,
 onAdd: (() -> Unit)?
) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Icon(
 icon,
 contentDescription = title,
 modifier = Modifier.size(40.dp)
 )

 Spacer(modifier = Modifier.width(16.dp))

